import React, { Fragment, useState, useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { Responsive, Grid } from 'semantic-ui-react';
import './Head.css';
import authContext from './AuthContext';


const renderLogin = (user) => {
    const isLoggedIn = (!user && !user.userId && (user.expirationDate > new Date()));
    console.log(user);
    return <NavLink to="/login" activeClassName="active" className="ui item">{isLoggedIn ? 'Login' : 'Logout'}</NavLink>
}

const Head = () => {
    const [ state, dispatch ] = useContext(authContext);
    const { user } = useState(null);
    console.log(user);
    return (
        <Fragment>
            <div className="ui violet secondary pointing menu">
                <NavLink to="/" exact activeClassName="active" className="item"><h1>Post List</h1></NavLink>
                <div className="right menu align-grid">
                    <Responsive as={Grid} columns={3} minWidth={768}>
                        <NavLink to="/new" activeClassName="active" className="ui item">New Post</NavLink>
                        {renderLogin(user)}
                        <NavLink to="/singup" activeClassName="active" className="ui item">Sing Up</NavLink>
                    </Responsive>
                </div>
            </div>
        </Fragment>
    );

    useEffect(() => {
        renderLogin(user);
    }, [user]) // Run one time
}


export default Head;
