import { LOGIN, LOGOUT } from '../actions/actionTypes';

export default (state = null, action) => {
    switch (action.type) {
        case LOGIN: {
            return { ...state, ...action.payload };
        }
        case LOGOUT: {
            return null;
        }
        default:
            return state;
    }
}
